class componente{


	public function verificaAutenticacao(){
		
		@session_start();
		
		if($_SESSION['usuario'] == '') {
			header('Location:http://www.ultramoderndance.com.br/sistema/index.php');
			exit;
		}

	}

	public function perfil($array, $perfil){
		
		//$administrativo = 1;
		//$aluno = 2;
		//$professor = 3;

		foreach ($array as $key => $value) {
			
			if($value == $perfil){
				return true;
			}else{
				return false;
			}
		}
		
	}


}

